  return (
    <div className="app">
      <div className="game">
        {currentColorArrangement.map((candyColors, index : number) => (
          <img
            key={index}
            style={{backgroundColor: candyColors}}
            alt={candyColors}
          />
        ))}
      </div>
    </div>
  )
}

export default App
