    <div className="app">
      <div className="game">
        {currentColorArrangement.map((candyColors, index) => (
          <img
            key={index}
            style={{backgroundColor: candyColors}}
            alt={candyColors}
            data-id={index}
            draggable={true}
            onDragStart={dragStart}
            onDragOver={(e) => e.preventDefault()}
            onDragEnter={(e) => e.preventDefault()}
            onDragLeave={(e) => e.preventDefault()}
            onDrop={dragDrop}
            onDragEnd={dragEnd}
          />
        ))}
      </div>
    </div>
  )
}

export default App
